<?php
// Calculate primes in the range (2, 1000)
$primes = array();
for ($n=2; $n<1000; $n++) {
	$factors = 0;
	for ($i=2; $i<$n; $i++) {
		if ($n / $i === floor($n / $i)) {
			$factors++;
		}
	}
	if ($factors === 0) {
		$primes[] = $n;
	}
}
echo '<ul><li>' . implode('</li><li>', $primes) . '</li></ul>';
?>
